        generator = CqlScaffoldGenerator(input_file)

        indicator_artifact = pd.read_excel(
            input_file, sheet_name="Indicator definitions"
        )

        # Test the first row
        cql_template = generator.generate_cql_template(indicator_artifact.iloc[0])

        print(cql_template)


